@import url('https://fonts.googleapis.com/css2?family=Karla:ital,wght@0,300;0,400;0,500;0,600;0,700;0,800;1,200;1,300;1,400;1,500;1,600;1,700;1,800&display=swap');
@tailwind base;
@tailwind components;
@tailwind utilities;

.vhd-top-menu-expanded {
  @apply absolute top-10 z-50 w-full bg-white text-center animate-slideDown;
}

.vhd-top-menu-item {
  @apply py-1 cursor-pointer hover:text-gray-800 relative after:lg:absolute after:lg:bottom-0 after:lg:left-0 after:lg:bg-coffee-200 hover:after:lg:w-full after:lg:h-0.5 after:lg:w-0 after:lg:transition-all after:lg:duration-300 after:lg:ease-in-out;
}

.vhd-top-menu-item-active {
  @apply text-gray-800;
}

.vhd-icon {
  @apply w-5 h-5 inline-block;
}

.vhd-badge-circle {
  @apply text-xs text-center rounded-full px-1.5;
}

.vhd-subheadline {
  @apply flex items-center justify-center mb-24;
}

.vhd-subheadline-deco-line {
  @apply w-8 h-px bg-gray-200;
}

.vhd-subheadline-label {
  @apply uppercase mx-4 tracking-widest text-gray-500 font-medium text-[12px] text-center;
}

.vhd-button {
  
}
